#include <verilated.h>
#include <verilated_vcd_c.h>
#include <iostream>
#include "/home/yanyusong/OneStudentOneChip/DigitalCircuitsExperiments/DCE05/register8/obj_dir/Vregister8.h"

VerilatedContext* contextp = NULL;
VerilatedVcdC* tfp = NULL;

static Vregister8* top;

void step_and_dump_wave()
{
	top -> eval();
	contextp -> timeInc(1);
	tfp -> dump(contextp -> time());
}

void sim_init()
{
	contextp = new VerilatedContext;
	tfp = new VerilatedVcdC;
	top = new Vregister8;
	contextp -> traceEverOn(true);
	top -> trace (tfp, 0);
	tfp -> open ("dump.vcd");
}

void sim_exit()
{
	step_and_dump_wave();
	tfp -> close();
}

